.settings-view .search-result {
  padding: 15px;
  margin-bottom: 10px;
  list-style-type: none;
  font-size: 1.2em;
  border-radius: 8px;
  border: 1px solid #3B4252;
  background-color: #3f4758;
  overflow: hidden;
  -webkit-user-select: none;
}
.settings-view .search-result:hover {
  background-color: #434c5e;
}
.settings-view .search-result:active {
  background-color: #3f4758;
}
.settings-view .search-result.disabled {
  background-color: #39404f;
}
.settings-view .search-result.disabled .body,
.settings-view .search-result.disabled .avatar,
.settings-view .search-result.disabled .author,
.settings-view .search-result.disabled .stats {
  opacity: 0.5;
}
.settings-view .search-result.col-lg-4 {
  min-width: 190px;
  padding: 10px 0;
  padding-left: 10px;
  padding-right: 10px;
}
.settings-view .search-result .search-name {
  margin-right: 0.8em;
  font-weight: bolder;
  color: #ECEFF4;
  cursor: pointer;
}
.settings-view .search-result .search-id {
  font-size: 0.8em;
  margin-right: 10px;
  display: block;
  /* Force newline on item to avoid complex line handling */
  opacity: 0.5;
}
.settings-view .search-result .search-description {
  color: #D8DEE9;
  overflow: hidden;
  min-height: 38px;
  max-height: 38px;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  /* number of lines to show */
  -webkit-box-orient: vertical;
}
.settings-view .search-result .card-name {
  font-weight: 300;
  margin: 0 0 0.2em 0;
  font-size: 1.2em;
  line-height: 1.4;
}
.settings-view .search-result .search-package-name {
  margin-right: 0.2em;
  color: #ECEFF4;
  font-size: 1.2em;
  line-height: 1.4;
}
.settings-view .search-result .search-result-icon {
  margin-left: 0.2em;
}
