}
.symbols-view .list-group .character-match {
  color: #ECEFF4;
  font-weight: bold;
}
atom-panel.modal .symbols-view .primary-line {
  display: flex !important;
  flex-direction: row;
  align-items: center;
}
atom-panel.modal .symbols-view .primary-line.icon::before {
  margin-left: 0;
}
atom-panel.modal .symbols-view .primary-line .name {
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: pre;
  margin-right: auto;
}
atom-panel.modal .symbols-view .primary-line .badge-container {
  display: block;
  flex-grow: 0;
  display: flex;
  flex-direction: row;
}
atom-panel.modal .symbols-view .primary-line .badge-container .badge:not(:last-child) {
  margin-right: 5px;
}
atom-panel.modal .symbols-view .primary-line .badge-symbol-tag {
  text-transform: capitalize;
}
atom-panel.modal .symbols-view .secondary-line {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: baseline;
  max-width: 100%;
}
atom-panel.modal .symbols-view .secondary-line .location {
  display: block;
}
atom-panel.modal .symbols-view .secondary-line .context {
  display: block;
  text-overflow: ellipsis;
  overflow: hidden;
  flex-grow: 0;
}
